const globalMascot = $items('Read Global Mascot Config')[0]?.json?.values?.[0]?.[0] || '';
const contentRows = $input.all();
const pendingItems = [];

for (let i = 0; i < contentRows.length; i++) {
  const row = contentRows[i].json;
  const rowNumber = row.row_number;
  const rawContent = (row['Raw Content'] || '').toString().trim();
  const rowMascot = (row['Mascot(Opt)'] || '').toString().trim();
  const status = (row['Status'] || '').toString().trim();

  if (rawContent && status !== 'Success' && status !== 'Processing') {
    pendingItems.push({
      json: {
        rowNumber: rowNumber,
        rawContent: rawContent,
        rowMascot: rowMascot,
        globalMascot: globalMascot,
        currentStatus: status
      }
    });
  }
}

if (pendingItems.length > 0) {
  return [pendingItems[0]];
} else {
  return [{ json: { _noWork: true, message: 'No pending items' } }];
}

const contentResp = $('OpenAI: Write Content').first();
const promptResp = $('OpenAI: Generate Image Prompt').first();

let contentText = '';
let imagePrompt = '';

if (contentResp?.json?.output) {
  contentText = contentResp.json.output;
} else if (contentResp?.json?.text) {
  contentText = contentResp.json.text;
} else if (contentResp?.json?.content) {
  contentText = contentResp.json.content;
} else if (contentResp?.json?.message?.content) {
  contentText = contentResp.json.message.content;
} else if (typeof contentResp?.json === 'string') {
  contentText = contentResp.json;
}

if (promptResp?.json?.output) {
  imagePrompt = promptResp.json.output;
} else if (promptResp?.json?.text) {
  imagePrompt = promptResp.json.text;
} else if (promptResp?.json?.content) {
  imagePrompt = promptResp.json.content;
} else if (promptResp?.json?.message?.content) {
  imagePrompt = promptResp.json.message.content;
} else if (typeof promptResp?.json === 'string') {
  imagePrompt = promptResp.json;
}

const payloadData = $('Prepare Payload').first().json;

return $input.all().map((item, index) => ({
  json: {
    ...payloadData,
    contentText: contentText.trim(),
    imagePrompt: imagePrompt.trim(),
    hasContent: contentText.trim() ? true : false
  },
  pairedItem: item.pairedItem || { item: index }
}));

